class Solution {
    void CountSort(vector<int>& nums, int m){
        int n = nums.size();
        vector<int> v(n), count(10, 0);
        for(int i=0; i<n; ++i){
            count[(nums[i]/m)%10]++;
        }
        for(int i=1; i<10; ++i){
            count[i] += count[i-1];
        }
        for(int i=n-1; i>=0; --i){
            v[count[(nums[i]/m)%10]-1] = nums[i];
            count[(nums[i]/m)%10]--;
        }
        for(int i=0; i<n; ++i){
            nums[i] = v[i];
        }
        return;
    }
    void Radix_Sort(vector<int>& nums){
        int n = nums.size();
        int m = -1;
        for(int i=0; i<n; ++i){
            m = max(m, nums[i]);
        }
        for(int i=1; m/i>0; i *= 10){
            CountSort(nums, i);
        }
        return;
    }
public:
    int maximumGap(vector<int>& nums) {
        int n = nums.size();
        Radix_Sort(nums);
        int mx = 0;
        for(int i=1; i<n; ++i){
            mx = max(mx, nums[i]-nums[i-1]);
        }
        return mx;
    }
};
