class Solution {
    class pair{
        int f;
        int s;
        pair(int f , int s){
            this.f = f;
            this.s = s;
        }
    }
    int inf = Integer.MAX_VALUE;
    int nrk = Integer.MIN_VALUE;
    public int maximumGap(int a[]) {
        int n = a.length;
        if(n < 2) return 0;
        pair v[] = new pair[n - 1];
        for(int i = 0; i < n - 1; i++){
            v[i] = new pair(inf , nrk);
        }
        int mx = nrk;
        int mn = inf;
        for(int e : a){
            mx = Math.max(mx , e);
            mn = Math.min(mn , e);
        }
        double gap = (double)(mx - mn) / (double)(n - 1);
        for(int e : a){
            if(e == mn || e == mx) continue;
            int idx = (int)((double)(e - mn) / (double)gap);
            v[idx].f = Math.min(v[idx].f , e);
            v[idx].s = Math.max(v[idx].s , e);
        }
        int prev = mn;
        int max = 0;
        for(int i = 0; i < n - 1; i++){
            if(v[i].f == inf) continue;
            max = Math.max(max , v[i].f - prev);
            prev = v[i].s;
        }
        return Math.max(max , mx - prev);
    }
}

