public int maximumGap(int[] nums) {

	TreeSet<Integer> st = Arrays.stream(nums)
								.boxed()
								.collect(Collectors.toCollection(TreeSet::new));

	int maxGap = 0;
	int prev = -1;

	for (int num : st)  {                         
		if (prev == -1) {
			prev = num;
			continue;
		} else {
			maxGap = Math.max(maxGap, num - prev);
			prev = num;
		}
	}

	return maxGap;
}
