			int[] bucketMax = new int[n];
			//is no of gaps of size interval
			//[i] - bcket can have min + i * interval to min + (i + 1) * interval - 1

			Arrays.fill(bucketMin, Integer.MAX_VALUE);
			Arrays.fill(bucketMax, Integer.MIN_VALUE);
