def maximumGap(self, nums: List[int]) -> int:
	mn, mx, n = min(nums), max(nums), len(nums)
	if len(nums)<2 or mx==mn:
		return 0
	step = math.ceil((mx-mn)/(n-1))
	buckets = [[] for _ in range((mx-mn)//step+1)]
	for n in nums:
		idx = (n-mn)//step
		if not buckets[idx]:
			buckets[idx] = [n, n]
		else:
			a, b = buckets[idx]
			buckets[idx] = [min(a, n), max(b, n)]
	buckets = [b for b in buckets if b]
	return max(buckets[i+1][0]-buckets[i][1] for i in range(len(buckets)-1))
