class Solution {

    static int prev = -1;
    static int MAX_BITS = 32;
    static int powersOf2 [] = new int[MAX_BITS];
    static int maxDiff = 0;

    public int maximumGap(int[] nums) {
        powersOf2[0] = 1;
        for(int i = 1; i < MAX_BITS; i++) {
            powersOf2[i] = 2*powersOf2[i - 1];
        }


        Node ROOT = new Node();
        for(int i = 0; i < nums.length ; i++) {
            addToTrieTree(ROOT, nums[i]);
        }

        preOrder(ROOT, MAX_BITS - 1, 0);
        return maxDiff;
    }

      public void preOrder(Node node, int index, int num) {

        if (index == -1) {
            if (prev != -1) {
                maxDiff = Math.max(maxDiff, num - prev);
            }
            prev = num;
        }

        if(node.left != null) {
            preOrder(node.left, index - 1, num);
        }

        if(node.right != null) {
            preOrder(node.right, index - 1, num + powersOf2[index]);
        }

    }

    public void addToTrieTree(Node root, int num) {

        int binary[] = getBinary(num);

        for(int i = 0; i < binary.length ; i++) {

            if(binary[i] == 0) {
                if (root.left == null) {
                    root.left = new Node();
                }
                root = root.left;

            } else {
                if (root.right == null) {
                    root.right = new Node();

                }
                root = root.right;
            }
        }
    }

    public int[] getBinary(int num) {

        int binary[] = new int[MAX_BITS];
        int index = MAX_BITS - 1;
        while (num > 0)  {
            binary[index--] = num % 2;
            num /= 2;
        }
        return binary;
    }
}

