class Solution {
public:
    void countSort(vector<int> &nums, const int exp){
        int N = nums.size();
        int output[N];
        int count[10] = {};

		//For exp = 100, num = 19403, num/exp = 194, (num/exp)%10 = 4
        for(const int &num : nums){
            int idx = (num/exp)%10;
            count[idx]++;
        }

		 // Change count[i] so that count[i] now contains actual
		//  position of this digit in output[]
        for(int i = 1; i < 10; i++){
            count[i] += count[i-1];
        }

        for(int i = N-1; i >= 0; i--){
            int idx = (nums[i]/exp)%10;
            int outputIdx = --count[idx];
            output[outputIdx] = nums[i];
        }

        for(int i = 0; i < N; i++){
            nums[i] = output[i];
        }
        return;
    }
    void radixSort(vector<int> &nums){
        int maxi = *max_element(begin(nums), end(nums));
        //For maxi = 19403, exp = 1, 10, 100, 1000, 10000
		// Do counting sort for every digit. Note that instead of passing digit number, exp is passed. exp is 10^i
        // where i is current digit number
        for(int exp = 1; maxi/exp > 0; exp *= 10){
            countSort(nums, exp);
        }
    }
    int maximumGap(vector<int>& nums) {
        radixSort(nums);
        int res = 0;
        int N = nums.size();
        for(int i = 1; i < N; i++){
            res = max(res, nums[i] - nums[i-1]);
        }
        return res;
    }
};
