// The assamtion is that the larger gap will be >= (max-min)/n.
// By deviding the range for block smaller than (max-min)/n we make sure that the 2 related numbers not in the same block;
class Solution {
public:
    int maximumGap(vector<int>& nums) {
        int n = nums.size();
        if (n<2) return 0;
        int min_ = INT_MAX;
        int max_ = INT_MIN;
        for(auto& num : nums)
        {
            max_ = max(max_,num);
            min_ = min(min_,num);
        }

        if (min_ == max_) return 0;
        int block_size = (max_ - min_)/(n-1);
        if (block_size == 0) block_size = 1;
        int m = (max_ - min_)/block_size+1;
        vector<pair<int,int>> blocks(m,{INT_MAX,INT_MIN});

        for(auto num: nums)
        {
            int ix;
            if (num == min_) ix = 0;
            else
                ix = (num - min_)/block_size;

            blocks[ix].first = min(blocks[ix].first,num);
            blocks[ix].second = max(blocks[ix].second,num);
        }

        int ans = 0;
        int p = min_;
        for(auto& block : blocks)        
        {
            if (block.first == INT_MAX) continue;
            ans = max(ans,block.first-p);
            p = block.second;
        }

        return ans;
    }
};

