class Solution {
    public int maximumGap(int[] nums) {
        int gap=0;
        int n =nums.length;
        int d = Integer.MIN_VALUE;
        int c=0;
        for(int i=0;i<n;i++){
            if(nums[i] > d) d=nums[i];
        }
        while(d>0){
            c++;
            d=d/10;
        }
        d=1;
        while(c-->0){
            int count[] = new int[10];
            int[] arr = new int[n];
            for(int i=0;i<n;i++){
                ++count[(nums[i]/d)%10];
            }
            for(int i=1;i<10;i++) count[i]+=count[i-1];
            for(int i=n-1;i>=0;i--){
                arr[--count[(nums[i]/d)%10]] = nums[i];
            }
            nums=arr;
            d=d*10;
        }
        for(int i=1;i<n;i++) if(nums[i]-nums[i-1]>gap) gap=nums[i]-nums[i-1];
        return gap;
    }
}
