class Solution:
    def maximumGap(self, nums: List[int]) -> int:
        if len(nums) < 2:
            return 0
        elif len(nums) == 2:
            return max(nums) - min(nums)

        self.max_N_digits = max([len(str(num)) for num in nums])
        nums = self.radixSort(nums, 0)

        return max([nums[i]-nums[i-1] for i in range(1, len(nums))])

    def radixSort(self, nums, d):
        if d == self.max_N_digits:
            return nums
        bucket = {i:[] for i in range(10)}
        key = lambda x: int((x/(10**d))%10)

        for num in nums:
            bucket[key(num)].append(num)

        new_nums = []
        for i in range(10):
            new_nums += bucket[i]

        return self.radixSort(new_nums, d+1)
