class Solution {

        public int nthUglyNumber(int n) {
            ListNode current = new ListNode(1);
            ListNode n2 = current;
            ListNode n3 = current;
            ListNode n5 = current;

            for (int i = 2; i <= n; i++) {
                int min = Math.min(n2.val * 2, Math.min(n3.val * 3, n5.val * 5));
                current.next = new ListNode(min);
                current = current.next;
