class Solution {
public:
    bool isUgly(int n)
    {
        if(n==0)
        return false;
        while(n%2==0)
        n/=2;
        while(n%3==0)
        n/=3;
        while(n%5==0)
        n/=5;
        return n==1;
    }
    int nthUglyNumber(int n) {
    int i=1,c=0;
         while(c!=n)
         {
             if(isUgly(i))
             {
                 c++;
             }
             if(c==n)
             break;
             i++;
         }
        return i;
    }
};
