class Solution {
    public int nthUglyNumber(int n) {
        n--;
        int val=1,num=2;
        while(n>0){
            if(isUgly(num)){
                val=num;
                n--;
            }
            num++;
        }
        return val;
    }

    public boolean isNumUgly(int num, int arr[], int index){
        if(num==1)
            return true;
        if(index>=arr.length)
            return false;
        else if(num%arr[index]!=0)
            return isNumUgly(num,arr,index+1);

        num=num/arr[index];

        boolean b1 = isNumUgly(num,arr,index);
        if(b1)
            return true;

        Boolean b2 = isNumUgly(num,arr,index+1);
        if(b2)
            return true;

        Boolean b3 = isNumUgly(num,arr,index+2);
        if(b3)
            return true;

        return false;
    }

    public boolean isUgly(int num){
        if(num==0)
            return false;
        if(num==1)
            return true;
        int arr[] = {2,3,5};
        return isNumUgly(num,arr,0);
    }
}
