class Solution {
public:
    int nthUglyNumber(int n) {
//      Base case 
        if(n==1)
            return 1;
        vector<int>ugly(n);
//         1 st ugly is one
        ugly[0]=1;
//         Now new ugly numbers can be generated by 
//         multiplying previous ones by 2,3 and 5
//         But we need to generate them in increasing order


//         t2 is the index of ugly no. to which we have to multiply 2
        int t2=0,t3=0,t5=0;

        for(int i=1;i<n;i++){
//         So we multiply with three of them and the minimum among them is our next ugly no.
            int next = min({2*ugly[t2],3*ugly[t3],5*ugly[t5]});

//  Suppose we go this ugly no. by multiplying with 2 then 
//  Next time we will multipy 2 with the next greater ugly no. we have
//  Ex: first we mutipied 2 by 1 (@index 0) next time we will multiply 2 by 2 (@index 1)
            if(next == 2*ugly[t2])
                t2++;
            if(next == 3*ugly[t3])
                t3++;
            if(next == 5*ugly[t5])
                t5++;
            ugly[i]=next;
        }

        return ugly[n-1];
    }
};
```

