class Solution {
public:
    int nthUglyNumber(int n){
        vector<long long> ans;
        priority_queue<long long,vector<long long>,greater<long long>> Q;
        unordered_map<long long,bool> visited;
        Q.push(1);
        visited[1]=true;
        while (!Q.empty()&&ans.size()<n){
            long long x=Q.top();
            Q.pop();
            ans.push_back(x);
            if (!visited[x*2]){
                Q.push(x*2);
                visited[x*2]=true;
