    }

    private boolean canIWinFrom(int maxChoosableInteger, int desiredTotal, boolean[] chosen) {
        if (desiredTotal <= 0) {
            return false;
        }

        for (int i = 1; i <= maxChoosableInteger; i++) {
            if (chosen[i]) {
                continue;
            }
            chosen[i] = true;
            if (!canIWinFrom(maxChoosableInteger, desiredTotal - i, chosen)) {
                chosen[i] = false;
                return true;
            }
            chosen[i] = false;
        }
        return false;
    }
}
