```
class Solution {
public:
    bool canIWin(int maxChoosableInteger, int desiredTotal) {
        // Win in one move: select desiredTotal if available within available integers
        if (maxChoosableInteger >= desiredTotal) return true;

        // Impossible game, cannot reach desiredTotal as playing all numbers does not reach the desiredTotal
        int totalSum = maxChoosableInteger * (maxChoosableInteger+1) /2;
        if (desiredTotal > totalSum) return false;

        // Limit game: desiredTotal only reachable by playing *all* integers
        if (desiredTotal == totalSum){
            // I can win if maxChoosableInteger is odd: all values have to be played in turn.
			// This means that both players will, in turn, have to pick all the available integers to exactly reach desiredTotal at the very end
			// I thus win if it's my turn to play when time has come to play the last integer.
			// As I start te game, this is true if there is an odd number of integers to play
            if (maxChoosableInteger % 2 ==0) return false;
            else return true;
        }

        // Regular game
		// Recursion will be used to examine games
		// See details below
        return isWin(maxChoosableInteger, desiredTotal, 0 /*used values - none*/);
    }


private:
    // Memoization: make sure to remember the outcome of already seen game configurations
	// We identify a game by the desiredTotal, and then by the set of used values
    unordered_map<int, unordered_map<int, bool>> seenGames;

    /**
     * @ Brief determines if I can win the game, given set of already used values
     *
     * @param maxChoosableInteger: max possible integer I can play
     * @param desiredTotal: amount to reach or exceed via successive sums
     * @param usedValues: set of already played integers. This set is represented as a bit field
     *        we know there are maximum 20 possible integers we can use
     *        an int would have at least 32 bits here, so can hold 20 bits to represent used values
     *        e.g. if bit 1<<num is set, it means num has been used
     */
    bool isWin(int maxChoosableInteger, int desiredTotal, int usedValues){
        if (desiredTotal<=0) return false;

        if (seenGames.count(desiredTotal) != 0){
            // desired total already seen
            // check if it was with same set of used values
            if (seenGames[desiredTotal].count(usedValues) != 0){
                // already seen this game, return outcome
                return seenGames[desiredTotal][usedValues];
            }
        }

        // General valid game
        for (int n=maxChoosableInteger; n>=1; --n){
            // do not consider already played values
            if ( (1<<n & usedValues) == 0){
                // First, check if I can win in 1 move
                if (n>=desiredTotal) return true;

                // otherwise:
                if (not isWin(maxChoosableInteger, desiredTotal-n, usedValues|1<<n)){
                    // if not win with one less integer, then I have a win now as it's my turn to pick
                    seenGames[desiredTotal][usedValues] = true;
                    return true;
                }
            }
        }
        seenGames[desiredTotal][usedValues] = false;
        return false;
    }

};
```

