class Solution(object):
    def canIWin(self, maxChoosableInteger, desiredTotal):
        choosable = tuple(range(1, maxChoosableInteger + 1))
        if sum(choosable) < desiredTotal: return False
        self.cache = {}
        return self.dfs(choosable, desiredTotal)

