class Solution {
    int desire;
    int range;
    Boolean[] dp;

    public boolean canIWin(int maxChoosableInteger, int desiredTotal) {
        if(maxChoosableInteger >= desiredTotal) return true;
        if((maxChoosableInteger + 1)*maxChoosableInteger / 2 < desiredTotal) return false;
        desire = desiredTotal;
        range = maxChoosableInteger;
        dp = new Boolean[1 << range];

        return dfs(0, 0);
    }

    private boolean dfs(int total, int state) {
        if(dp[state] != null) return dp[state];

        for(int i = 1; i <= range; i++) {
            int cur = 1 << (i-1);
            if((cur & state) == 0) {
                // Win in this round, or next round/player not win
                if(total + i >= desire || !dfs(total + i, state | cur)) {
                    dp[state] = true;
                    return true;
                }
            }
        }

        return dp[state] = false;
    }
}
