class Solution {
	bool memo[(1<<21) + 1] = {false};
	int n;

	bool dfs(int val, int k = 0){
		if(val <= 0)
			return false;

		if(memo[k] == true)
			return true;

		for(int i = 1; i <= n; ++i){
			int mask = 1<<i;

			if(k&mask)          // k&mask != 0 -> visited
				continue;

			if(dfs(val - i, k | mask) == false)    // if next player loses -> cur player wins
				return memo[k] = true;
		}

		return memo[k] = false;
	}

public:
	bool canIWin(int n, int t) {
	   int s = n*(n+1)/2;

		if(n >= t)              // first player choose n to win
			return true;        

		if(s < t)               // second player chooses largest values to win
			return false;

		if(s == t)              // whoever picks last number wins
			return n%2;

		this->n = n;

		return dfs(t);
	}
};

