class Solution:
    def canIWin(self, maxChoosableInteger: int, desiredTotal: int) -> bool:
        if (1+maxChoosableInteger)*maxChoosableInteger//2 < desiredTotal:
            return False
        usage = [0]*(1+maxChoosableInteger) # 0 for unused, usage[0] is dummy
        mem = {}
        return self.helper(0, usage, maxChoosableInteger, desiredTotal, mem)

    def helper(self, cur_val, usage, maxChoosableInteger, desiredTotal, mem):
        usage_tuple = tuple(usage)
        if usage_tuple in mem:
            return mem[usage_tuple]

        for n in range(1, maxChoosableInteger+1): # Iterate 1 ... maxChoosableInteger
            if usage[n]: # usage[n] == 1 means, n is used.
                continue
            if cur_val + n >= desiredTotal:
                mem[usage_tuple] = True
                return True
            usage[n] = 1
            # If we can pick a number that makes opponent lose, current player is a win
            if not self.helper(cur_val+n, usage, maxChoosableInteger, desiredTotal, mem):
                usage[n] = 0 # Remember to backtrack
                mem[usage_tuple] = True
                return True
            usage[n] = 0
        # If we cannot find a number thats make opponent lose, current play must lose
        mem[usage_tuple] = False
        return False
