class Solution {
public:
	bool canIWin(int maxChoosableInteger, int desiredTotal) {
		if (maxChoosableInteger >= desiredTotal)
			return true;

		int total = (1 + maxChoosableInteger) * maxChoosableInteger / 2;
		if (total < desiredTotal)
			return false;        

		return dfs(maxChoosableInteger, desiredTotal, 0);
	}

private:
	int memo[1 << 21];
	bool dfs(int maxChoosableInteger, int desiredTotal, int state)
	{           
		if (desiredTotal <= 0)
		{
			memo[state] = -1;
			return false;
		}

		if (memo[state] != 0)
			return (memo[state] == 1);            

		for (int i = 1; i <= maxChoosableInteger; i++)
		{
			int s = 1 << i;
			if (state & s)
				continue;

			if (!dfs(maxChoosableInteger, desiredTotal - i, state | s))
			{
				memo[state] = 1;
				return true;
			}
		}
		memo[state] = -1;
		return false;
