class Solution {
public:

    bool dfs( int player , vector<int>& maxa , int total ){
        if( total <= 0 ){
            if( player == 1 ){
                return true;
            }else{
                return false;
            }
        }
        bool ret = player ;
        for( int i = 1 ; i < maxa.size() ; i++ ){
            if( maxa[i] == 1 ){
                maxa[i] = 0;
                if( player == 0 ){
                    ret = ret || dfs( 1 , maxa , total-i ) ;
                }else{
                    ret = ret && dfs( 0 , maxa , total-i ) ;
                }

                maxa[i] = 1;

            }
        }

        return ret;   
    }

    bool canIWin(int n, int desiredTotal) {

        vector<int> maxa(n+1,1);

        if( desiredTotal == 0 ){
            return true;
        }else if( n*(n+1)/2 < desiredTotal) {
            return false;
        }

         return dfs( 0 , maxa , desiredTotal ) ;

    }
};
