class Solution {
    Map<Integer, Boolean> dp = new HashMap<>();

    public boolean canIWin(int maxChoosableInteger, int desiredTotal) {
        int arithmeticSum = (maxChoosableInteger * (maxChoosableInteger + 1)) / 2;
        if (arithmeticSum < desiredTotal) {
            return false;
        }
        for (int x = 1; x <= maxChoosableInteger; x++) {
            Set<Integer> seen = new HashSet<>();
            seen.add(x);
            if (dfs(x, maxChoosableInteger, desiredTotal, 1 << x, seen)) {
                return true;
            }
        }
        return false;

    }

    private boolean dfs(int sum, int max, int desired, int bitmask, Set<Integer> seen) {
        if (sum >= desired) {
            return true;
        }
        if (dp.containsKey(bitmask)) {
            return dp.get(bitmask);
        }
        for (int x = 1; x <= max; x++) {
            if (!seen.contains(x)) {
                Set<Integer> next = new HashSet<>(seen);
                next.add(x);
                if (dfs(sum + x, max, desired, bitmask ^ (1 << x), next)) {
                    dp.put(bitmask, false);
                    return false;
                }
            }
        }
        dp.put(bitmask, true);
        return true;
    }
}
