class Solution {
	bool probe(vector<char>& memo, int n, int mask, int total){
		int idx = n-1;
		while((1<<idx) & mask) idx--;
		if(idx+1 >= total) return true;

		for(int i=idx; i>=0; i--){
			if((1<<i) & mask) continue;

			int mask_ = (mask|(1<<i));
			if(memo[mask_] == '0') return true;
			if(memo[mask_] == '1') continue;

			bool ret = probe(memo, n, mask_, total-(i+1));
			memo[mask_] = ret ? '1' : '0';
			if(!ret) return true;
		}

		return false;
	}
public:
	bool canIWin(int n, int total) {
		int sum = (n*(n+1))>>1;
		if (sum < total){
			return false;
		}

		vector<char> memo(1<<n, 0);
		return probe(memo, n, 0, total);
	}
};

