class Solution(object):
    def canIWin(self, maxChoosableInteger, desiredTotal):
        """
        :type maxChoosableInteger: int
        :type desiredTotal: int
        :rtype: bool
        """

        def canWin(alt, desiredTotal, memo):
            if tuple(alt) in memo:
                return memo[tuple(alt)]

            #If it is possible to win by using a value from alternatives alt, 
            #then alt is a winning list.
            for v in alt:
                if desiredTotal - v <= 0:
                    memo[tuple(alt)] = True
                    return True

            #If there is an alt2 given to the other player that is loosing, 
            #then alt is a wining list. 
			#This for can be optimized by checking first the alt2 that are already in memo.
            for i in range(0, len(alt)):
                alt2 = alt[:i] + alt[i+1:]
                if not canWin(alt2, desiredTotal - alt[i], memo):
                    memo[tuple(alt)] = True
                    return True

            #Otherwise alt is loosing.
            memo[tuple(alt)] = False
            return False


        alt = range(1, maxChoosableInteger+1)

        if sum(alt) < desiredTotal:
            return False

        return canWin(alt, desiredTotal, {})
