class Solution {
public:
	/*
		we are using the integer ch where the ith bit denotes that the number i has been taken or not
		if the ith bit is on , it means that the number is available for us to take
		if the ith bit is off , it means that the number is not availbale for us to take

		for a given desired total, which number has been taken and which is not is enough to denote the state of the dp

	*/
	unordered_map<int, int> dp;
	bool dfs(int ch, int maxChose, int desiredTotal, int curTotal) {
		if (dp.find(ch) != dp.end()) return dp[ch];
		for (int i = 1; i <= maxChose; i++) {

			// if the ith bit is on , it means that this number is availabe for us to take
			if (ch & (1 << i)) {
				if (curTotal + i >= desiredTotal) return dp[ch] = true;
				bool cur = !dfs((ch & ~(1 << i)), maxChose, desiredTotal, curTotal + i); // opponent has the next turn, and in case it is not possible for him to win from this route, it is possible for me to win
				if (cur) return dp[ch] = true;
			}
		}

		return dp[ch] = false;

	}
	bool canIWin(int maxChose, int desiredTotal) {

		// all these corner case checks are important, else it will give wrong answer
		int sum = (maxChose * (maxChose + 1)) / 2 ;
		if (sum < desiredTotal) return false;

		if (desiredTotal <= 0) return true;
		if (sum == desiredTotal) return maxChose % 2;

		int ch = INT_MAX; // all bits are set in this

		bool ans = dfs(ch, maxChose, desiredTotal, 0);
		return ans;



	}
};
```

