An improved version of https://leetcode.com/problems/can-i-win/discuss/1454684/Java-DP-Solution-Recursion-with-Memory

```
class Solution {
    public boolean canIWin(int maxChoosableInteger, int desiredTotal) {
        int sum = (maxChoosableInteger * (maxChoosableInteger + 1)) >> 1;
        if(sum < desiredTotal){
            return false;
        }

        if(desiredTotal <= 0){
            return true;
        }

        HashMap<Integer, Boolean> cache = new HashMap<Integer, Boolean>();
        boolean[] used = new boolean[maxChoosableInteger + 1];
        return canWin(maxChoosableInteger, 0, desiredTotal, used, cache, 0);
    }

    private boolean canWin(int maxChoosableInteger, int totalTillNow, int desiredTotal, boolean[] used, HashMap<Integer, Boolean> cache, int bitMap){
        // total till now has exceeded desiredTotal
        // meaning I have already lost the game
        if(totalTillNow >= desiredTotal){
            return false;
        }else{
            if(!cache.containsKey(bitMap)){
                boolean status = false;
                for(int i = 1; i <= maxChoosableInteger; i++){
                    if(!used[i]){
                        used[i] = true;
                        // my opponent should not win for me to be able to win
                        status = !canWin(maxChoosableInteger, totalTillNow + i, desiredTotal, used, cache, bitMap | (1 << i));  // adjust the bitMap accordingly by OR operation      
                        used[i] = false;

                        // if possible for my opponent to lose - means i can win
                        if(status){
                            break;
                        }
                    }
                }

                cache.put(bitMap, status);
            }

            return cache.get(bitMap);
        }
    }
}
