class Solution {
public:
    vector<int> cache;
    int maxv;
    // dp(state, remain):
    // If both players do their best(if there is any chance to win, they choose to win.),
    // TRUE: The player on the current turn may win at least one of them.
    // FALSE: The player on the current turn never wins.
    bool dp(int state, int remain) {

        if (remain <= 0) // The current player loses.
            return false;

        if (cache[state] != -1)
            return cache[state];

        for (int n = 0; n < maxv; ++n) {
            if (state & (1 << n))
                continue;
            if (!dp(state | (1 << n), remain - n - 1)) // The player on the next turn never wins --> There are cases where the player of the current turn wins.
                return cache[state] = true;
        }

        return cache[state] = false;
    }

    bool canIWin(int maxChoosableInteger, int desiredTotal) {
        int sum = ((maxChoosableInteger+1) * maxChoosableInteger) / 2;
        if (sum < desiredTotal)
            return false;
        if (desiredTotal == 0)
            return true;
        maxv = maxChoosableInteger;
        cache = vector<int>(1 << maxChoosableInteger, -1);
        return dp(0, desiredTotal);
    }
};
