        if(d<=0 && !turn){
            return true;
        }

        else if(d<=0){
            return false;
        }

        else if(turn){
            if(dp[visited]==0){
                int temp = 1;
                int counter = 1;
                while(counter<=m){
                    if((temp&visited)==0){
                        boolean check = recursion(m, d-counter, !turn, visited|temp, dp);
                        if(check){
                            dp[visited] = 1;
                            break;
                        }
                    }
                    temp = temp<<1;
                    counter++;
                }
                if(dp[visited]==0){
                    dp[visited] = -1;
                }
            }
            if(dp[visited]==1){
                return true;
            }
            return false;
