class Solution {

    public Map<Integer , Boolean> dp;

    public boolean canIWin(int maxChoosableInteger, int desiredTotal) {
        dp = new HashMap<>();
        int limit = (maxChoosableInteger * (maxChoosableInteger + 1)) / 2;
        if(desiredTotal > limit) return false;
        if(desiredTotal == 0) return true;

        return !util(desiredTotal , new boolean[maxChoosableInteger + 1] , maxChoosableInteger , 0);
    }

    public boolean util(int sum , boolean[] visited , int maxChoosableInteger , int mask) {
        if(sum <= 0) return true;
        boolean flag = false;
        if(dp.containsKey(mask)) return dp.get(mask);

        for(int i = 1 ; i <= maxChoosableInteger ; i++) {
            if(!visited[i]) {
                visited[i] = true;
                flag = flag || util(sum - i , visited , maxChoosableInteger , (1 << i) | mask);
                visited[i] = false;
            }
        }

        dp.put(mask , !flag);
        return !flag;
    }
}
