class Solution {
    int[] memo;

    public boolean canIWin(int maxChoosableInteger, int desiredTotal) {
        int maxTotal = (maxChoosableInteger + 1) * maxChoosableInteger / 2;
        if (maxTotal < desiredTotal) return false;
        memo = new int[1<<maxChoosableInteger];
        return win(desiredTotal, maxChoosableInteger, 0);
    }

    private boolean win(int total, int n, int bitmask) {
        if (memo[bitmask] > 0) return true;
        else if (memo[bitmask] < 0) return false;

        for (int i = n; i >= 1; i--) {
            if ((bitmask & (1 << i - 1)) > 0) continue;
            if (i >= total) {
                memo[bitmask] = 1;
                return true;
            }
            if (!win(total - i, n, bitmask | (1 << i - 1))) {
                memo[bitmask] = 1;
                return true;
            }
        }
        memo[bitmask] = -1;
        return false;
    }
}
