                return True if 1-player ==0 else False 
            elif not can:
                return False
            else:
                if player ==0:
                    for i,e in enumerate(can):
                        if minimax(can[:i]+can[i+1:],score+e,1):
                            return True
                    return False
                else:
                    for i,e in enumerate(can):
                        if not minimax(can[:i]+can[i+1:],score+e,0):
                            return False
                    return True
