class Solution {
    int[] numbersPool;
    int desiredTotal;
	Map<String, Boolean> map;

    public String formKey(){
        StringBuilder builder = new StringBuilder();
        for(int i=0; i<numbersPool.length; i++){
            builder.append(numbersPool[i]);
        }
        return builder.toString();
    }

    //max ||
    //min &&

    public boolean canIWin(int sumSoFar, boolean isMax){
        if(sumSoFar >= desiredTotal){
            return !isMax;
        }
		String curr = formKey();
		if(map.containsKey(curr)) return map.get(curr);
        boolean res;
        if(isMax)
            res = false;
        else
            res = true;
        for(int i=1; i<numbersPool.length; i++){
            if(numbersPool[i] == 0){
                numbersPool[i] = 1;

                if(isMax)
                    res = res || canIWin(sumSoFar + i, false);
                else
                    res = res && canIWin(sumSoFar + i, true);

                numbersPool[i] = 0;
            }
        }
		map.put(curr, res);
        return res;
    }

    public boolean canIWin(int maxChoosableInteger, int desiredTotal) {

        if (desiredTotal<=0) return true;
        if (maxChoosableInteger*(maxChoosableInteger+1)/2<desiredTotal) return false;
        if (maxChoosableInteger >= desiredTotal) return true;
		map = new HashMap<>();
        numbersPool = new int[maxChoosableInteger+1];
        this.desiredTotal = desiredTotal;
        return canIWin(0,true);
    }
}
