class Solution(object):
    def canIWin(self, maxChoosableInteger, desiredTotal):
        """
        :type maxChoosableInteger: int
        :type desiredTotal: int
        :rtype: bool
        """
        if maxChoosableInteger>=desiredTotal:
            return True
        if (maxChoosableInteger+1)*maxChoosableInteger/2<desiredTotal:
            return False
        status = (1<<maxChoosableInteger) -1
        dp = [dict() for _ in range(desiredTotal+1)]
        return self.minMax(status,dp,desiredTotal,maxChoosableInteger)
    def minMax(self,status,dp,goal,maxInt):
        if status in dp[goal]:
            return dp[goal][status]
        for i in range(1,maxInt+1):
            if status & 1<<(i-1):
                if i>=goal or not self.minMax(status^1<<(i-1),dp,goal-i,maxInt):
                    dp[goal][status] = True
                    return True
        dp[goal][status] = False
        return False
