/*
char[] used,  array to store the status of remain positions
Map<String, Boolean> cache, map to store the visited status
time: 0(n!) when n is the max number
*/
class Solution {
    public boolean canIWin(int max, int N) {
        if(N<=0) return true;
        if((max+1)*max/2<N) return false;
        char[] used= new char[max+1];
        Arrays.fill(used,'1');
        Map<String, Boolean> cache = new HashMap<>();
        return dfs(used, cache,N);
    }
    boolean dfs(char[] used,Map<String, Boolean> cache, int n){
        if(n<=0) return false;
        String key = new String(used);
        if(cache.containsKey(key)) return cache.get(key);
        boolean ans= false;
        for(int i=1;i<used.length; i++){
          if(used[i]=='1'){
              used[i]='0';
              if(!dfs(used, cache, n-i)) ans=true;
              used[i]='1';
              if(ans) break;
          }   
        }
        cache.put(new String(used),ans);
        return ans;
    }
}
