class Solution {
    Map<List<Integer>,Boolean> memo=new HashMap<>();
    public boolean canIWin(int maxChoosableInteger, int desiredTotal) {
        if(desiredTotal<=maxChoosableInteger) return true;
        if((maxChoosableInteger+1)*maxChoosableInteger/2<desiredTotal) return false;
        List<Integer> choosableInt=new ArrayList<>();
        for(int i=1;i<=maxChoosableInteger;i++)
            choosableInt.add(i);
        for(int i=1;i<=maxChoosableInteger;i++){
            choosableInt.remove((Object)i);
            List<Integer> newSet=new ArrayList<>(choosableInt);
            if(!dp(newSet,desiredTotal-i,memo)) return true;
            choosableInt.add((Integer)i);
        }

        return false;
    }

    private boolean dp(List<Integer> choosableInt,int desiredTotal,Map<List<Integer>,Boolean> memo){
        if(choosableInt==null) return false;
        for(Integer i:choosableInt)
            if(desiredTotal<=i) return true;


        for(ListIterator<Integer> it=choosableInt.listIterator();it.hasNext();){
            Integer i=it.next();

            List<Integer> list=new ArrayList<>(choosableInt);
            list.remove((Object)i);
            if(memo.containsKey(list)){
                if(!memo.get(list)) return true;
            }else{
                Boolean b=dp(list,desiredTotal-i,memo);
                memo.put(list,b);
                if(!b) return true;
            } 

        }

        return false;
    }
}
