class Solution {
    private HashMap<Integer, Boolean> map;
    private boolean[] passed;
    public boolean canIWin(int maxChoosableInteger, int desiredTotal) {
        map = new HashMap<>();
        passed = new boolean[maxChoosableInteger+1];
        Arrays.fill(passed, false);
        if (desiredTotal <= maxChoosableInteger) {
            return true;
        }
        if ((1 + maxChoosableInteger) * maxChoosableInteger / 2 < desiredTotal) {
            return false;
        }
        return helper(desiredTotal);
    }

    private boolean helper(int sum) {
        if (sum <= 0) {
            return false;
        }
        int code = hashcode();
        if (map.containsKey(code)) {
            return map.get(code);
        }
        boolean res = false;
        for (int num = 1; num < passed.length && !res; ++num) {
            if (passed[num]) {
                continue;
            }
            passed[num] = true;
            res |= !helper(sum - num);
            passed[num] = false;
        }
        map.put(code, res);
        return res;
    }

    private int hashcode() {
        int res = 0;
        for (int num = 1; num < passed.length; ++num) {
            if (passed[num]) {
                continue;
            }
            res |= (1 << num);
        }
        return res;
    }
}
