                int n = 1 << i;

                if((current & n)  == 0 && (sum+i >= total ||  !CanIWin(mapping,total,(current | n),max,sum+i))){
                    mapping.Add(current,true);
                    return true;
                }
            }
            mapping.Add(current,false);
            return false;
        }else{
            return mapping[current];
        }
    }
}
