import java.util.HashMap;
import java.util.Arrays;

class Solution {
    public int[] nums;
    public HashMap<String,Boolean> myMap;
    int count;

    public boolean canI(int sum,int total,int player){
        String myHash = ""+sum+"|"+player+"|"+Arrays.hashCode(nums);
        if(myMap.containsKey(myHash)){   
            return myMap.get(myHash);
        }
        if(count == 0){
            return false;
        }
        if(player == 0){
            for(int i = nums.length-1;i >= 1;i--){
                if(nums[i] == 1){
                    continue;
                }
                if(sum + i >= total){
                    myMap.put(myHash,true);
                    return true;
                }
                else{
                    nums[i] = 1;
                    count--;
                    if(canI(sum+i,total,1)){
                        myMap.put(myHash,true);
                        nums[i] = 0;
                        count++;
                        return true;
                    }
                    count++;
                    nums[i] = 0;
                }
            }
            myMap.put(myHash,false);
            return false;
        }
        else{
            for(int i = nums.length-1;i >= 1;i--){
                if(nums[i] == 1){
                    continue;
                }
                if(sum + i >= total){
                    myMap.put(myHash,false);
                    return false;
                }
                else{
                    count--;
                    nums[i] = 1;
                    if(!canI(sum+i,total,0)){
                        myMap.put(myHash,false);
                        nums[i] = 0;
                        count++;
                        return false;
                    }
                    count++;
                    nums[i] = 0;
                }
            }
            myMap.put(myHash,true);
            return true;
        }
    }

    public boolean canIWin(int maxChoosableInteger, int desiredTotal) {
        nums = new int[maxChoosableInteger+1];
        for(int i = 1;i <= maxChoosableInteger;i++){
            nums[i] = 0;
        }
        myMap = new HashMap<String,Boolean>();
        count = maxChoosableInteger;
        return canI(0,desiredTotal,0);
    }
}
```

