/**
 * @param {number} maxChoosableInteger
 * @param {number} desiredTotal
 * @return {boolean}
 */
var canIWin = function(maxChoosableInteger, desiredTotal) {
    if (desiredTotal <= 0) {
        return true;
    }  
    if (maxChoosableInteger * (maxChoosableInteger + 1) / 2 < desiredTotal) {
        return false;
    }
    const memo = new Map();
    function canWin(remainingTotal, usedNumbers) {
        if (memo.has(usedNumbers)) {
            return memo.get(usedNumbers);
        }
        for (let i = 1; i <= maxChoosableInteger; i++) {
            const mask = 1 << i;
            if ((usedNumbers & mask) === 0) { 
                if (i >= remainingTotal || !canWin(remainingTotal - i, usedNumbers | mask)) {
                    memo.set(usedNumbers, true);
                    return true;
                }
            }
        }
        memo.set(usedNumbers, false);
        return false;
    }
    return canWin(desiredTotal, 0);
};
```

