class Solution(object):
    def win(self,arr,score,desiredTotal):
        if score+arr[-1] >= desiredTotal:
            return True
        if arr in self.memo:
            return self.memo[arr]
        for i in range(len(arr)):
            if not self.win(arr[:i]+arr[i+1:],score+arr[i],desiredTotal):
                self.memo[arr] = True
                return True
            self.memo[arr] = False
        return False

    def canIWin(self, maxChoosableInteger, desiredTotal):
        if desiredTotal < 0:
            return True
        if (maxChoosableInteger*(maxChoosableInteger+1))/2 < desiredTotal:
            return False
        if maxChoosableInteger > desiredTotal:
            return True
        if maxChoosableInteger+1 == desiredTotal:
            return False
        if desiredTotal < maxChoosableInteger*2:
            return True
        self.memo = {}
        return self.win(tuple(range(1, maxChoosableInteger+1)),0,desiredTotal)
