class Solution {
public:
    int bottomUpDp(string &word1, string &word2, int m, int n, vector<vector<int>>& dp){
        // Initialize base cases
        for (int i = 0; i <= m; i++) {
            dp[i][0] = i;   //rows
        }
        for (int j = 0; j <= n; j++) {
            dp[0][j] = j;   //columns
        }

        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= n; j++) {
                if (word1[i - 1] == word2[j - 1]) {
                    dp[i][j] = dp[i - 1][j - 1];
                } else {
                    dp[i][j] = 1 + min(dp[i - 1][j - 1], min(dp[i - 1][j], dp[i][j - 1]));
                // Carefully observe the recursive code is converted to iterative approach which tries to find the solution in dp table instead of recursive calls
                }
            }

        }

        return dp[m][n];
    }

