*C++: Top-Down with Memo*
```
class Solution {
public:
    int minDistance(string A, string B) {
        return go(A, B, A.size(), B.size());
    }
private:
    using Memo = unordered_map<string, int>;
    int go(string& A, string& B, int i, int j, Memo&& memo = {}) {
        stringstream ss; ss << i << "," << j;
        auto key = ss.str(); // 🔑
        if (memo.find(key) != memo.end())
            return memo[key]; // 🤔 memo
        if (!i || !j)
            return memo[key] = i + j;
        return memo[key] = min({
            go(A, B, i - 1, j - 1, move(memo)) + int(A[i - 1] != B[j - 1]), // ✅ match xor ❌ mismatch
            go(A, B, i - 1, j, move(memo)) + 1, // ❌ insertion
            go(A, B, i, j - 1, move(memo)) + 1  // ❌ deletion
