public static int helperBU_Eff(String s1, String s2){
		int[] strg = new int[s2.length() + 1];
		for(int i = s1.length();i >= 0;i--){
			int save = 0;
			for(int j = s2.length();j >= 0;j--){
				if(i == s1.length()){
					strg[j] = s2.length() - j;
					continue;
				}
				if(j == s2.length()){
					save = strg[j];
					strg[j] = s1.length() - i;
					continue;
				}
				if(s1.charAt(i) == s2.charAt(j)){
					int temp = save;
					save = strg[j];
					strg[j] = temp;
				}else{
					int insert = strg[j + 1];
					int delete = strg[j];
					int replace = save;
					save = strg[j];
					strg[j] = Math.min(insert, Math.min(delete, replace)) + 1;
				}
			}
		}
		return strg[0];
	}
