int solve(string& a, string& b,int i,int j ,vector<vector<int>>& dp)
{
      int ans=0;
    if(a.length()==i)
    {
        return b.length()-j;
    }
    if(b.length()==j)
    {
        return a.length()-i;
    }
    if(dp[i][j]!=-1)
    {
        return dp[i][j];
    }
    if(a[i]==b[j])
    {
        return solve(a,b,i+1,j+1,dp);
    }

    else
