        int insert= 1 + solve(a,b,i,j+1,dp);

        int deleteans= 1 + solve(a,b,i+1,j,dp);

        int replace= 1 + solve(a,b,i+1,j+1,dp);
        ans = min(insert , min(deleteans , replace));
    }
    return dp[i][j]=ans;

}
