class Solution {
public:

    vector<vector<int>>dp;

    int recurs(string& word1, string& word2,int i,int j)
    {
        if(i>=word1.size()&&j>=word2.size())
             return 0;

        if(i>=word1.size())
             return word2.size()-j; // all remaining need to be added

        if(j>=word2.size())
             return word1.size()-i; // all remaining need to be removed

        if(dp[i][j]!=-1)
              return dp[i][j];

        if(word1[i]==word2[j])
             return dp[i][j]=recurs(word1,word2,i+1,j+1);
        else
        {
            int a=recurs(word1,word2,i+1,j+1)+1;//replace
            int b=recurs(word1,word2,i+1,j)+1;//delete
            int c=recurs(word1,word2,i,j+1)+1;//insert
            c=min(b,c);
            return dp[i][j]=min(a,c);
        }


    }








    int minDistance(string word1, string word2) {
        dp.resize(word1.size(),vector<int>(word2.size(),-1));

        return recurs(word1,word2,0,0);

    }
};
