class Solution {
    public int minDistance(String word1, String word2) {
        return recur(word1,word2,0,0);
    }

    int recur(String word1, String word2, int index1, int index2) {
        int n1 = word1.length(), n2 = word2.length();
        if(n2==index2) return n1-index1;
        if(n1==index1) return n2-index2;

        if(word1.charAt(index1)== word2.charAt(index2)) {
            return recur(word1,word2,index1+1,index2+1);
        } else {
            int c1 = 1+recur(word1, word2,index1,index2+1); //insert
            int c2 = 1+recur(word1, word2,index1+1,index2); //delete
            int c3 = 1+recur(word1, word2,index1+1,index2+1); //replace
            return Math.min(c1,Math.min(c2,c3));
        }
    }
}
