    int minDistance(string s, string t) {
        int n = s.size(), m = t.size();
        vector<int> dp(m + 1), temp(m + 1);

        for(int j=0; j<=m; j++) dp[j] = j;

        for(int i=1; i<=n; i++) {
            temp[0] = i;
            for(int j=1; j<=m; j++) {
                if(s[i - 1] == t[j - 1]) temp[j] = dp[j - 1];
                else temp[j] = 1 + min(dp[j - 1], min(temp[j - 1], dp[j]));
            }
            dp = temp;
        }
        return dp[m];
    }
};
